import React, { Component } from "react";

export default class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      lname: "",
      pass: ""
    };
  }

  //validation
  handleValidation = e => {
    const fields = this.state.fields;
    const errors = this.state.errors;
    var isValid = true;

    if (!fields.fname) {
      var isValid = false;
      errors.fname = "cannont be empty";
    } else if (!fields.fname) {
      if (!fields.fname.match(/^[a-zA-Z]+$/)) {
        var isValid = false;
        errors.fname = "Letters only";
      }
    }
    if (!fields.lname) {
      var isValid = false;
      errors.lname = "cannont be empty";
    } else if (!fields.lname) {
      if (!fields.lname.match(/^[a-zA-Z]+$/)) {
        var isValid = false;
        errors.lname = "Letters only";
      }
    }
    if (!fields.email) {
      var isValid = false;
      errors.email = "cannont be empty";
    } else if (!fields.email) {
      if (!fields.email.match(/\S+@\S+\.\S+/)) {
        var isValid = false;
        errors.email = "Invalid Email";
      }
    }
    if (!fields.pass) {
        var isValid = false;
        errors.pass = "cannont be empty";
      } else if (!fields.pass) {
        if (!fields.pass.match(/^[a-zA-Z0-9]+$/)) {
          var isValid = false;
          errors.pass = "NO ";
        }
      }
  };

  handleChange = e => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({
      [name]: value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    alert(this.state.fname);
  };

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group row">
            <label className="col-4"></label>
            <div className="col-8">
              <input
                onChange={this.handleChange}
                id="fname"
                name="fname"
                placeholder="First Name"
                type="text"
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row">
            <label className="col-4"></label>
            <div className="col-8">
              <input
                onChange={this.handleChange}
                id="lname"
                name="lname"
                placeholder="Last Name"
                type="text"
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row">
            <label className="col-4"></label>
            <div className="col-8">
              <input
                onChange={this.handleChange}
                id="email"
                name="email"
                placeholder="Email"
                type="email"
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row">
            <label className="col-4"></label>
            <div className="col-8">
              <input
                onChange={this.handleChange}
                id="pass"
                name="pass"
                placeholder="Enter Your Password"
                type="password"
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row">
            <label className="col-4"></label>
            <div className="col-8">
              <input
                id="pass"
                name="pass"
                placeholder="Repeat Your Password"
                type="password"
                className="form-control"
              />
            </div>
          </div>
          <div className="form-group row">
            <div className="offset-4 col-8">
              <button name="submit" type="submit" className="btn btn-primary">
                Submit
              </button>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
